mapper.select:
select * from campus_recruitment_info
WHERE
university_name=#{admin_university}

	AND hold_date>=#{today}
	AND start_time>=#{time}


	AND hold_date<#{today}
	AND start_time<#{time}

SELECT * FROM campus_recruitment_info
WHERE (university_name LIKE CONCAT('%', #{keyword}, '%') OR company_name LIKE CONCAT('%', #{keyword}, '%') OR locations LIKE concat('%', #{keyword}, '%') OR university_short_name LIKE CONCAT('%', #{keyword}, '%'))
and hold_date>=#{date}
